// DirectLogById retrieves a row from 'ms.direct_log' as a DirectLog.
//
// Generated from index 'direct_log_Id_pkey'.
func DirectLogById(db XODB, id int) (*DirectLog, error) {
	var err error

	// sql query
	const sqlstr = `SELECT ` +
		`Id, ToUserId, ChatId, LogTypeEnumId, DataPB, DataJson, Created ` +
		`FROM ms.direct_log ` +
		`WHERE Id = ?`

	// run query
	XOLog(sqlstr, id)
	dl := DirectLog{
		_exists: true,
	}

	err = db.QueryRow(sqlstr, id).Scan(&dl.Id, &dl.ToUserId, &dl.ChatId, &dl.LogTypeEnumId, &dl.DataPB, &dl.DataJson, &dl.Created)
	if err != nil {
		XOLogErr(err)
		return nil, err
	}

	OnDirectLog_LoadOne(&dl)

	return &dl, nil
}
