//
// Generated from index 'comments_Id_pkey'.
func CommentById(db XODB, id int) (*Comment, error) {
	var err error

	// sql query
	const sqlstr = `SELECT ` +
		`Id, UserId, PostId, Text, CreatedTime ` +
		`FROM ms.comments ` +
		`WHERE Id = ?`

	// run query
	XOLog(sqlstr, id)
	c := Comment{
		_exists: true,
	}

	err = db.QueryRow(sqlstr, id).Scan(&c.Id, &c.UserId, &c.PostId, &c.Text, &c.CreatedTime)
	if err != nil {
		return nil, err
	}

	OnComment_LoadOne(&c)

	return &c, nil
}
